import { combineReducers } from 'redux';
import {useSelector } from 'react-redux'


export default combineReducers({
    toComplete:ToCompleteReducer,
    completed:CompletedReducer,

})

function ToCompleteReducer(toComplete=[], action){
    let DIMENSIONS=useSelector(state=>state.dimensions);
    let STATERANGE=useSelector(state=>state.stateRange);
    if(action.type === "TRY_REMOVE"){
        if (verifyRemove(action.cardToRemove)){
            let newToComplete = [];
            for(const card of toComplete){
                if (isSameCard(card, action.cardToRemove[0]) || isSameCard(card, action.cardToRemove[1]) || isSameCard(card, action.cardToRemove[2])) {
                    continue;
                }
                newToComplete.push(card);
            }
            return newToComplete;
        }
        else{
            return toComplete;
        }
    }
    else if (action.type === "START_GAME"){

        return GenerateInitialCards()
    }
    else{
        return toComplete;
    }
}

function CompletedReducer(completed=[], action){
    if(action.type === "TRY_REMOVE"){
        if (verifyRemove(action.cardToRemove)){
            return [...completed, ...action.cardToRemove];
        }
        else{
            return completed;
        }
    }
    else if (action.type === "START_GAME"){
        return [];
    }
    else{
        return completed;
    }
}

const GenerateInitialCards=(props) => {
    // let DIMENSIONS=useSelector(state=>state.dimensions);
    // let STATERANGE=useSelector(state=>state.stateRange);
    const DIMENSIONS = this.props.dimensions;
    const STATERANGE = this.props.stateRange;
    let i = 0;
    let initialState = [[]];
    while (i < DIMENSIONS) {
      let temp = [];
      let card;
      for (let j=0;j<STATERANGE;j++) {
          temp.push([...card, j]);
          initialState.forEach((item, index)=> temp.push([...item, j]))
      }
      initialState = temp;
      i++;
    }
    return initialState
  }

export function verifyRemove(cardToRemove) {
    let numDifferent = 0;
    for (const i=0;i<cardToRemove[0].size;i++) {
        let map = new Map();
        for(const card of cardToRemove) {
            map.set(card[i], 1);
        }
        if (map.size == 2) {
            return false;
        }
    }
    return true;
}

function isSameCard(card1, card2){
    for(let i=0;i<card1.size;i++){
        if (card1[i] != card2[i]) {
            return false;
        }
    }
    return true;
}
