import React from 'react';
import {connect} from "react-redux";
import { useSelector } from 'react-redux';
import './App.css';
import Card from "./Card";


class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      toComplete:[],
      completed:[],
      dimensions:4,
      stateRange:3,
      numView:12,
    };
  }
  render(){
    return(
    <div className="display">
    <div className="cards">
    {this.props.toComplete.slice(0,this.props.numView).map((value, index) => <Card className="card" type={value} key={index}/>)}
  </div>
  {/* <div className="cards">
      <Card className="card" type={[1,2,3,4]}/>
      <Card className="card" type={[1,2,3,4]}/>
      <Card className="card" type={[1,2,3,4]}/>
      <Card className="card" type={[1,2,3,4]}/>
    </div>
    <div className="cards">
      <Card className="card" type={[1,2,3,4]}/>
      <Card className="card" type={[1,2,3,4]}/>
      <Card className="card" type={[1,2,3,4]}/>
      <Card className="card" type={[1,2,3,4]}/>
    </div> */}
    <div className="button">
    <button><h3>Home</h3></button>
    <button onClick={()=> this.props.dispatch({type:"START_GAME"})}><h3>Play</h3></button>
    <button><h3>Rule</h3></button>
    </div>
  </div>
    
    )
  }
  
}

let mapDispatchToProps = function(dispatch, props) {
  return {
    dispatch:dispatch 
  }
}

let mapStateToProps = function(state, props) {
  return {
    toComplete:state.toComplete,
    numView:state.numView,
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(App)
