import React from 'react';
import {connect} from "react-redux";
import { useSelector } from 'react-redux';
import './App.css';
import Card from "./Card";
import {isInToRemove} from "./Reducer";
import {Link} from 'react-router-dom';


export default class Home extends React.Component {
  constructor(props){
    super(props);
  }

  render(){
    return(
    <div className="rule">
    <button> <Link exact to={""}>Home</Link></button>
    <button> <Link exact to={"/difficulty"}>Difficulty</Link></button>
    <button> <Link exact to={"/rule"}>Rule</Link></button>
    <button onClick={()=> {this.props.dispatch({type:"EASY"}); this.props.dispatch({type:"START_GAME"});}}><h3>Easy</h3></button>
    <button onClick={()=> {this.props.dispatch({type:"MEDIUM"}); this.props.dispatch({type:"START_GAME"});}}><h3>Medium</h3></button>
    <button onClick={()=> {this.props.dispatch({type:"HARD"}); this.props.dispatch({type:"START_GAME"});}}><h3>Hard</h3></button>
    <p>  A set of 3 cards where each of the features are either all the same or all different.
   </p>
  </div>  
    )
  }
}
